import { createRouter, createWebHistory } from 'vue-router';
import { auth, database } from '../firebaseConfig';
import Login from '../views/auth/Login.vue';
import Register from '../views/auth/Register.vue';
import Home from '../views/Home.vue';
import store from '../store';
import createEvent from '../views/foodDona/admin/createFoodEve.vue';
import { ref, get } from 'firebase/database';
import trendingEvents from '../views/foodDona/user/trendingEvents.vue'
import FoodEvent from '../views/foodDona/user/foodEvent.vue'
import Search from '../views/foodDona/user/foodBankSear.vue';
import aboutUs from '../views/aboutUs.vue'

import createCrowdfundingEvent from '../views/crowdFund/admin/createCrowdfundingEvent.vue'
import Crowdfunding from '../views/crowdFund/user/crowdfundingMain.vue'
import CrowdfundCheckout from '../views/crowdFund/user/crowdfundCheckout.vue'
import SuccessCrowdfund from "@/views/crowdFund/user/SuccessCrowdfund.vue";
import ErrorCrowdfund from "@/views/crowdFund/user/ErrorCrowdfund.vue";

import approveFoodEve from '../views/foodDona/admin/approveFoodEve.vue';
import profile from  '../views/auth/profile.vue';
import myDonations from '../views/myDonations.vue';
import deliveryDriverCreation from '../views/foodDona/admin/createDeliveryDriver.vue';
import approveGroupRequest from '../views/foodDona/user/groupDonations/approveGroupRequest.vue';
import ImageUpload from '../views/foodDona/admin/ImageUpload.vue';



const routes = [
    {
        path: '/auth/login',
        name: 'login',
        component: Login,
        meta: { requiresAuth: false },
    },
    {
        path: '/auth/register',
        name: 'Register',
        component: Register,
        meta: { requiresAuth: false },
    },
    {
        path: '/myDonations',
        name: "myDonations",
        component: myDonations,
        meta: { requiresAuth: true, requiredRole: 'user'},
    },
    {
        path: '/',
        name: 'Home',
        component: Home,
    },
    {
        path: '/aboutUs',
        name: 'aboutUs',
        component: aboutUs,

    },
    {
        path: '/auth/profile',
        name: 'Profile',
        component: profile,
        meta: { requiresAuth: true }
    },
    {
        path: '/foodeve/creation',
        name: 'createEvent',
        component: createEvent,
        meta: { requiresAuth: true, requiredRole: 'foodBank' },
    },
    {
        path: '/foodeve/trending',
        name: 'foodEventsTrending',
        component: trendingEvents,
        meta: { requiresAuth: true, requiredRole: 'user' },
    },
    {
        path: '/foodeve/:id',
        name: "foodEvent",
        component: FoodEvent,
        // children: [
        //     {
        //         path: 'groupDonation/create',
        //         name: 'createGroupDonation',
        //         component: CreateGroupDonation,
        //     }
        // ],
        meta: { requiresAuth: true, requiredRole: 'user' },
    },
    {
        path: '/search',
        name: 'Search',
        component: Search,
        meta: { requiresAuth: true, requiredRole: 'user' },
    },
    {
        path: '/crowdfunding',
        name: 'Crowdfunding',
        component: Crowdfunding,
        meta: { requiresAuth: true, requiredRole: 'user' },
    },
    {
        path: '/crowdfunding/:id',
        name: "CrowdfundCheckout",
        component: CrowdfundCheckout,
        meta: { requiresAuth: true, requiredRole: 'user' },
    },
    {
        path: '/crowdfunding/:id/success',
        name: "SuccessCrowdfund",
        component: SuccessCrowdfund,
        meta: { requiresAuth: true, requiredRole: 'user' },
    },
    {
        path: '/crowdfunding/error',
        name: "ErrorCrowdfund",
        component: ErrorCrowdfund,
        meta: { requiresAuth: true, requiredRole: 'user' },
    },
    {
        path: '/crowdfunding/creation',
        name: 'createCrowdfundingEvent',
        component: createCrowdfundingEvent,
        meta: { requiresAuth: true, requiredRole: 'foodBank' },
    },
    {
        path: '/foodeve/approve',
        name: 'approveFoodDonation',
        component: approveFoodEve,
        meta: { requiresAuth: true, requiredRole: 'foodBank' },
    },
    {
        path: '/foodbank/delivery/create',
        name: 'createDeliveryDriver',
        component: deliveryDriverCreation,
        meta: { requiresAuth: true, requiredRole: 'foodBank' },
    },
    {
        path: '/foodeve/groupDonations/approve',
        name: 'approveGroupRequestComponent',
        component: approveGroupRequest,
        meta: { requiresAuth: true, requiredRole: 'user' },
    },
    {
        path: '/test',
        name: 'test',
        component: ImageUpload,
        meta: { requiresAuth: true },
    }
]


const router = createRouter({ 
    history: createWebHistory(),
    routes
})

router.beforeEach((to, from, next) => {
    const requiresAuth = to.matched.some((record) => record.meta.requiresAuth);
    const requiredRole = to.meta.requiredRole;
    auth.onAuthStateChanged((user) => {
        if (requiresAuth && !user) {
            // If the route requires authentication and the user is not logged in, redirect to the login page
            next('/auth/login');
        } else {
            if (requiredRole && user) {
                const userRef = ref(database, `users/${user.uid}/role`);
                get(userRef).then((snapshot) => {
                    const userRole = snapshot.val();
                    //console.log("User Role from Database:", userRole);
                    if (userRole !== requiredRole) {
                        //console.log("User doesn't have the required role, redirecting.");
                        next('/');
                    } else {
                        //console.log("User has the required role, proceeding to the route.");
                        next();
                    }
                }).catch((error) => {
                    //console.error("Error fetching user role:", error);
                    next('/');
                });
            } else {
                //console.log("No required role or no user role defined, proceeding to the route.");
                next();
            }
        }
    });
})


export default router
